"}, {"fieldtype": "Section Break", "label": "بيانات العميل"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<div style="
    width: 100%;
    margin-top: 1px;
    margin-bottom: 1px;
    padding: 0;
    background: #fff;
    border-radius: 8px;
    border: 2px solid #000; /* تم تعديل سمك الحدود هنا */
    box-shadow: 0 1px 4px rgba(255,182,49,0.07);
    font-family: 'Cairo', Tahoma, Arial, sans-serif;
    direction: rtl;
    overflow: hidden;
    box-sizing: border-box;
">
    <table style="
        width: 100%;
        border-collapse: collapse; /* تم إبقاء هذا على collapse حيث كان في الكود الأصلي لهذا البلوك */
        margin: 0;
        padding: 0;
        line-height: 1.03;
        table-layout: auto;
    ">
        <tbody>
            {% if doc.customer_name or doc.name %}
            <tr style="
                font-size: 0.92em;
                white-space: nowrap;
            ">
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">اسم العميل</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">{{ doc.customer_name or "" }}</td>
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">رقم عرض السعر</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">{{ doc.name }}</td>
            </tr>
            {% endif %}

            {% set address_name = frappe.db.get_value("Dynamic Link", {"link_doctype": "Customer", "link_name": doc.party_name}, "parent") %}
            {% set address_line1 = "" %}
            {% set city = "" %}
            {% set pincode = "" %}
            {% if address_name %}
                {% set address_values = frappe.db.get_value("Address", address_name, ["address_line1", "city", "pincode"]) %}
                {% if address_values %}
                    {% set address_line1 = address_values[0] %}
                    {% set city = address_values[1] %}
                    {% set pincode = address_values[2] %}
                {% endif %}
            {% endif %}
            {% if address_line1 or city or pincode or doc.transaction_date %}
            <tr style="
                font-size: 0.92em;
                white-space: nowrap;
            ">
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">العنوان</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if address_line1 or city or pincode %}
                        {{ address_line1 or '' }}{% if city %}, {{ city }}{% endif %}{% if pincode %}, {{ pincode }}{% endif %}
                    {% endif %}
                </td>
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">تاريخ العرض</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if doc.transaction_date %}
                        {% set days_ar = ['الإثنين', 'الثلاثاء', 'الأربعاء', 'الخميس', 'الجمعة', 'السبت', 'الأحد'] %}
                        {% set dt = frappe.utils.get_datetime(doc.transaction_date) %}
                        {% set weekday = dt.weekday() %}
                        {{ days_ar[weekday] }} {{ "%02d"|format(dt.day) }}-{{ "%02d"|format(dt.month) }}-{{ dt.year }}
                    {% endif %}
                </td>
            </tr>
            {% endif %}

            {% set customer_doc = frappe.get_doc("Customer", doc.party_name) %}
            {% if customer_doc.custom_vat_registration_number or doc.valid_till %}
            <tr style="
                font-size: 0.92em;
                white-space: nowrap;
            ">
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">الرقم الضريبي</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if customer_doc.custom_vat_registration_number %}
                        {{ customer_doc.custom_vat_registration_number }}
                    {% endif %}
                </td>
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">مدة صلاحية العرض</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if doc.valid_till %}
                        {% set days_ar = ['الإثنين','الثلاثاء','الأربعاء','الخميس','الجمعة','السبت','الأحد'] %}
                        {% set valid_dt = frappe.utils.get_datetime(doc.valid_till) %}
                        {% set valid_weekday = valid_dt.weekday() %}
                        حتى {{ days_ar[valid_weekday] }} {{ "%02d"|format(valid_dt.day) }}-{{ "%02d"|format(valid_dt.month) }}-{{ valid_dt.year }}
                    {% endif %}
                </td>
            </tr>
            {% endif %}

            {% if customer_doc.custom_cr_number or doc.contact_email %}
            <tr style="
                font-size: 0.92em;
                white-space: nowrap;
            ">
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">السجل التجاري</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if customer_doc.custom_cr_number %}
                        {{ customer_doc.custom_cr_number }}
                    {% endif %}
                </td>
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">البريد الإلكتروني</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if doc.contact_email %}
                        {{ doc.contact_email }}
                    {% endif %}
                </td>
            </tr>
            {% endif %}

            {% if customer_doc.custom_700_number or doc.contact_mobile %}
            <tr style="
                font-size: 0.92em;
                white-space: nowrap;
            ">
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">الرقم الموحد</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if customer_doc.custom_700_number %}
                        {{ customer_doc.custom_700_number }}
                    {% endif %}
                </td>
                <td style="
                    color: #111;
                    text-align: right;
                    font-weight: bold;
                    padding: 2px;
                ">رقم الجوال</td>
                <td style="
                    text-align: right;
                    padding: 2px;
                ">
                    {% if doc.contact_mobile %}
                        {{ doc.contact_mobile }}
                    {% endif %}
                </td>
            </tr>
            {% endif %}
        </tbody>
    </table>
</div>"}, {"fieldtype": "Section Break", "label": "جملة تقديمية"}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% if doc.custom_description %}
